mapper.select:
select * from us
where us_id!='admin' order by us_id, us_regdate
limit #{pageStart},#{pageSize}

select * from ent
order by own_state
limit #{pageStart},#{pageSize}

mapper.update:
update cs
set cs_title=#{cs_title}, cs_content=#{cs_content},cs_update=now()
where cs_no=#{cs_no}

mapper.select:
select * from review 
order by rv_no, rv_regdate 
limit #{pageStart},#{pageSize}

select * from ac 
order by ac_no, ac_regdate 
limit #{pageStart},#{pageSize}

select ac_us_id 
from ac 
where ac_no=#{ac_no}

select ac_own_id 
from ac 
where ac_no=#{ac_no}

mapper.update:
update us join ac on us.us_id = ac.ac_us_id 
set us.us_stopdate=date_add(now(), interval #{stop} day),ac.ac_result=#{acVo.ac_result},us.us_state=1, ac.ac_state=1 
where ac.ac_no=#{acVo.ac_no}

update ent join ac on ent.own_id = ac.ac_own_id 
set ent.own_stopdate=date_add(now(), interval #{stop} day),ac.ac_result=#{acVo.ac_result},ent.own_state=1, ac.ac_state=1 
where ac.ac_no=#{acVo.ac_no}
